mapper.select:
SELECT r.r_id, r.user_id, r.r_shop,
	r.r_adrr, r.r_date, r.r_total
From receipt WHERE r.user_id = #{UserId}
	AND DATE_FORMAT(r.r_date, '%Y-%m') = #{yearMonth}
ORDER BY r.r_date ASC;

mapper.insert:
INSERT INTO save_items (receipt_id, name, price, count)
VALUES (
    #{receiptId},
    #{itemName},
    #{itemPrice},
    #{itemCount}
);
